282→  const isVisible = document.visibilityState === 'visible';
283→
284→  if (isVisible) {
285→    // Foreground tab: initialize immediately for good UX
286→    console.log('[Gemini Voyager] Foreground tab detected, initializing immediately');
287→    return 0;
288→  } else {
289→    // Background tab: add random delay to distribute load across multiple tabs
290→    const randomRange = BACKGROUND_TAB_MAX_DELAY - BACKGROUND_TAB_MIN_DELAY;
291→    const randomDelay = BACKGROUND_TAB_MIN_DELAY + Math.random() * randomRange;
292→    console.log(
293→      `[Gemini Voyager] Background tab detected, delaying initialization by ${Math.round(randomDelay)}ms`,
294→    );
295→    return randomDelay;
296→  }
297→}
298→
299→/**
300→ * Handle tab visibility changes
301→ */
302→function handleVisibilityChange(): void {
303→  if (document.visibilityState === 'visible' && !initialized) {
304→    // Tab became visible before initialization completed
305→    // Cancel any pending delayed initialization and start immediately
306→    if (initializationTimer !== null) {
307→      clearTimeout(initializationTimer);
308→      initializationTimer = null;
309→      console.log('[Gemini Voyager] Tab became visible, initializing immediately');
310→    }
311→    initializeFeatures();
312→  }
313→}
314→
315→// Main initialization logic
316→(function () {
317→  try {
318→    if (!hasValidExtensionContext()) return;
319→
320→    const onUnhandledRejection = (event: PromiseRejectionEvent) => {
321→      if (isExtensionContextInvalidatedError(event.reason)) {
322→        event.preventDefault();
323→      }
324→    };
325→    const onWindowError = (event: ErrorEvent) => {
326→      if (isExtensionContextInvalidatedError(event.error ?? event.message)) {
327→        event.preventDefault();
328→      }
329→    };
330→    window.addEventListener('unhandledrejection', onUnhandledRejection);
331→    window.addEventListener('error', onWindowError);
332→
333→    // Quick check: only run on supported websites
334→    const hostname = location.hostname.toLowerCase();
335→    const isSupportedSite =
336→      hostname.includes('gemini.google.com') ||
337→      hostname.includes('business.gemini.google') ||
338→      hostname.includes('aistudio.google.com') ||
339→      hostname.includes('aistudio.google.cn');
340→
341→    // Initialize KaTeX configuration early to suppress Unicode warnings
342→    // This must run before any formulas are rendered on the page
343→    if (isSupportedSite) {
344→      initKaTeXConfig();
345→      // Initialize i18n early to ensure translations are available
346→      initI18n().catch((e) => console.error('[Gemini Voyager] i18n init error:', e));
347→    }
348→
349→    // If not a known site, check if it's a custom website (async)
350→    if (!isSupportedSite) {
351→      // For unknown sites, check storage asynchronously
352→      chrome.storage?.sync?.get({ gvPromptCustomWebsites: [] }, (result) => {
353→        const customWebsites = Array.isArray(result?.gvPromptCustomWebsites)
354→          ? result.gvPromptCustomWebsites
355→          : [];
356→        const currentHost = hostname.replace(/^www\./, '');
357→
358→        const isCustomSite = customWebsites.some((website: string) => {
359→          const normalizedWebsite = website.toLowerCase().replace(/^www\./, '');
360→          return currentHost === normalizedWebsite || currentHost.endsWith('.' + normalizedWebsite);
361→        });
362→
363→        if (isCustomSite) {
364→          console.log('[Gemini Voyager] Custom website detected:', hostname);
365→          initializeFeatures();
366→        } else {
367→          // Not a supported site, exit early
368→          console.log('[Gemini Voyager] Not a supported website, skipping initialization');
369→        }
370→      });
371→      return;
372→    }
373→
374→    const delay = getInitializationDelay();
375→
376→    if (delay === 0) {
377→      // Immediate initialization for foreground tabs
378→      initializeFeatures();
379→    } else {
380→      // Delayed initialization for background tabs
381→      initializationTimer = window.setTimeout(() => {
382→        initializationTimer = null;
383→        initializeFeatures();
384→      }, delay);
385→    }
386→
387→    // Listen for visibility changes to handle tab switching
388→    document.addEventListener('visibilitychange', handleVisibilityChange);
389→
390→    // Setup cleanup on page unload to prevent memory leaks
391→    window.addEventListener('beforeunload', () => {
392→      try {
393→        window.removeEventListener('unhandledrejection', onUnhandledRejection);
394→        window.removeEventListener('error', onWindowError);
395→        if (folderManagerInstance) {
396→          folderManagerInstance.destroy();
397→          folderManagerInstance = null;
398→        }
399→        if (promptManagerInstance) {
400→          promptManagerInstance.destroy();
401→          promptManagerInstance = null;
402→        }
403→        if (quoteReplyCleanup) {
404→          quoteReplyCleanup();
405→          quoteReplyCleanup = null;
406→        }
407→        if (sendBehaviorCleanup) {
408→          sendBehaviorCleanup();
409→          sendBehaviorCleanup = null;
410→        }
411→      } catch (e) {
412→        if (isExtensionContextInvalidatedError(e)) {
413→          return;
414→        }
415→        console.error('[Gemini Voyager] Cleanup error:', e);
416→      }
417→    });
418→  } catch (e) {
419→    if (isExtensionContextInvalidatedError(e)) {
420→      return;
421→    }
422→    console.error('[Gemini Voyager] Fatal initialization error:', e);
423→  }
424→})();
