  1→import browser from 'webextension-polyfill';
  2→
  3→import { StorageKeys } from '@/core/types/common';
  4→
  5→import { type AppLanguage, normalizeLanguage } from './language';
  6→import { TRANSLATIONS, type TranslationKey, isTranslationKey } from './translations';
  7→
  8→type StorageAreaName = 'sync' | 'local';
  9→
 10→const readStorageValue = async (area: StorageAreaName): Promise<unknown> => {
 11→  try {
 12→    const storageArea = browser.storage?.[area];
 13→    if (storageArea?.get) {
 14→      const result = await storageArea.get(StorageKeys.LANGUAGE);
 15→      if (result && typeof result === 'object') {
 16→        return (result as Record<string, unknown>)[StorageKeys.LANGUAGE];
 17→      }
 18→    }
 19→  } catch {
 20→    // Fall through to chrome.* fallback below.
 21→  }
 22→
 23→  try {
 24→    const chromeStorage = chrome?.storage?.[area];
 25→    if (!chromeStorage?.get) return null;
 26→    return await new Promise<unknown>((resolve) => {
 27→      chromeStorage.get(StorageKeys.LANGUAGE, (result) => {
 28→        if (result && typeof result === 'object') {
 29→          resolve((result as Record<string, unknown>)[StorageKeys.LANGUAGE]);
 30→        } else {
 31→          resolve(null);
 32→        }
 33→      });
 34→    });
 35→  } catch {
 36→    return null;
 37→  }
 38→};
 39→
 40→const getStoredLanguage = async (): Promise<string | null> => {
 41→  const syncValue = await readStorageValue('sync');
 42→  if (typeof syncValue === 'string') return syncValue;
 43→  const localValue = await readStorageValue('local');
 44→  if (typeof localValue === 'string') return localValue;
 45→  return null;
 46→};
 47→
 48→/**
 49→ * Get the current language preference
 50→ * 1. First check user's saved preference in storage
 51→ * 2. Fall back to browser UI language
 52→ * 3. Default to English
 53→ */
 54→export async function getCurrentLanguage(): Promise<AppLanguage> {
 55→  const stored = await getStoredLanguage();
 56→  if (typeof stored === 'string') {
 57→    return normalizeLanguage(stored);
 58→  }
 59→
 60→  // Fall back to browser UI language
 61→  try {
 62→    const browserLang = browser.i18n.getUILanguage();
 63→    return normalizeLanguage(browserLang);
 64→  } catch {
 65→    return 'en';
 66→  }
 67→}
 68→
 69→/**
 70→ * Get translation for a key using the current language preference
 71→ * This function works in both React and non-React contexts (e.g., content scripts)
