.gv-folder-dialog-overlay {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.5);
  z-index: 2147483647;
  display: flex;
  align-items: center;
  justify-content: center;
}

.gv-folder-dialog {
  background-color: var(--folder-menu-bg);
  border: 1px solid var(--folder-border);
  border-radius: 12px;
  box-shadow: var(--folder-menu-shadow);
  padding: 0;
  min-width: 320px;
  max-width: 400px;
  max-height: 80vh;
  display: flex;
  flex-direction: column;
}

.gv-folder-dialog-title {
  color: var(--folder-text);
  font-size: 16px;
  font-weight: 500;
  padding: 16px 16px 12px 16px;
  border-bottom: 1px solid var(--folder-border);
}

.gv-folder-dialog-list {
  flex: 1;
  overflow-y: auto;
  padding: 8px;
  min-height: 200px;
  max-height: 400px;
}

.gv-folder-dialog-item {
  display: flex;
  align-items: center;
  gap: 8px;
  width: 100%;
  padding: 10px 12px;
  border: none;
  background: transparent;
  color: var(--folder-text);
  font-size: 14px;
  cursor: pointer;
  border-radius: 6px;
  transition: background-color 0.2s;
  text-align: left;
}

.gv-folder-dialog-item:not(:disabled):hover {
  background-color: var(--folder-hover-bg);
}

.gv-folder-dialog-item:disabled {
  cursor: not-allowed;
  opacity: 0.5;
