  1→/**
  2→ * Recents Hider - Elegant hide/show toggle for "my-stuff-recents-preview" section
  3→ *
  4→ * Design Philosophy:
  5→ * Instead of a popup toggle, we use a contextual "hover reveal" pattern where
  6→ * a subtle hide button appears on hover. When hidden, a minimal "peek bar"
  7→ * allows users to restore the section.
  8→ */
  9→import browser from 'webextension-polyfill';
 10→
 11→import { StorageKeys } from '@/core/types/common';
 12→
 13→import { getTranslationSync } from '../../../utils/i18n';
 14→
 15→// Constants
 16→const STYLE_ID = 'gv-recents-hider-style';
 17→const HIDDEN_CLASS = 'gv-recents-hidden';
 18→const PEEK_BAR_CLASS = 'gv-recents-peek-bar';
 19→const TOGGLE_BTN_CLASS = 'gv-recents-toggle-btn';
 20→const STORAGE_KEY = 'gvRecentsHidden';
 21→
 22→// Selectors - targeting the recents preview section
 23→const RECENTS_SELECTOR = '.my-stuff-recents-preview';
 24→
 25→let initialized = false;
 26→let observer: MutationObserver | null = null;
 27→
 28→/**
 29→ * Inject CSS styles for the hide/show functionality
 30→ */
 31→function injectStyles(): void {
 32→  if (document.getElementById(STYLE_ID)) return;
 33→
 34→  const style = document.createElement('style');
 35→  style.id = STYLE_ID;
 36→  style.textContent = `
 37→    /* Container for proper positioning */
 38→    .my-stuff-recents-preview {
 39→      position: relative;
 40→      transition: all 0.35s cubic-bezier(0.4, 0, 0.2, 1);
 41→    }
 42→
 43→    /* Toggle button - appears on hover */
 44→    .${TOGGLE_BTN_CLASS} {
 45→      position: absolute;
 46→      top: 8px;
 47→      right: 8px;
 48→      width: 28px;
 49→      height: 28px;
 50→      border-radius: 50%;
 51→      border: none;
 52→      background: var(--gm3-sys-color-surface-container-high, rgba(0, 0, 0, 0.06));
 53→      backdrop-filter: blur(8px);
 54→      cursor: pointer;
 55→      display: flex;
 56→      align-items: center;
 57→      justify-content: center;
 58→      opacity: 0;
 59→      transform: scale(0.8);
 60→      transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
 61→      z-index: 100;
 62→      color: var(--gm3-sys-color-on-surface-variant, #5f6368);
 63→    }
 64→
 65→    .${TOGGLE_BTN_CLASS}:hover {
 66→      background: var(--gm3-sys-color-surface-container-highest, rgba(0, 0, 0, 0.12));
 67→      transform: scale(1.1);
 68→    }
 69→
 70→    .${TOGGLE_BTN_CLASS}:active {
 71→      transform: scale(0.95);
 72→    }
 73→
 74→    .${TOGGLE_BTN_CLASS} svg {
 75→      width: 16px;
 76→      height: 16px;
 77→      transition: transform 0.2s ease;
 78→    }
 79→
 80→    /* Show button on container hover */
 81→    .my-stuff-recents-preview:hover .${TOGGLE_BTN_CLASS} {
 82→      opacity: 1;
 83→      transform: scale(1);
 84→    }
 85→
 86→    /* Hidden state - collapse with smooth animation */
 87→    .${HIDDEN_CLASS} {
 88→      max-height: 0 !important;
 89→      overflow: hidden !important;
 90→      opacity: 0 !important;
 91→      margin: 0 !important;
 92→      padding: 0 !important;
 93→      pointer-events: none !important;
 94→    }
 95→
 96→    /* Peek bar - minimal restore hint */
 97→    .${PEEK_BAR_CLASS} {
 98→      height: 6px;
 99→      margin: 8px 16px;
100→      border-radius: 3px;
101→      background: linear-gradient(
102→        90deg,
103→        transparent 0%,
104→        var(--gm3-sys-color-outline-variant, rgba(0, 0, 0, 0.08)) 20%,
105→        var(--gm3-sys-color-outline-variant, rgba(0, 0, 0, 0.08)) 80%,
106→        transparent 100%
107→      );
108→      cursor: pointer;
109→      transition: all 0.25s cubic-bezier(0.4, 0, 0.2, 1);
110→      position: relative;
111→      display: none;
112→    }
113→
114→    .${PEEK_BAR_CLASS}::after {
115→      content: '';
116→      position: absolute;
117→      top: 50%;
118→      left: 50%;
119→      transform: translate(-50%, -50%);
120→      width: 40px;
121→      height: 4px;
122→      border-radius: 2px;
123→      background: var(--gm3-sys-color-primary, #1a73e8);
124→      opacity: 0;
125→      transition: all 0.2s ease;
126→    }
127→
128→    .${PEEK_BAR_CLASS}:hover {
129→      height: 12px;
130→      background: linear-gradient(
131→        90deg,
132→        transparent 0%,
133→        var(--gm3-sys-color-primary-container, rgba(26, 115, 232, 0.12)) 15%,
134→        var(--gm3-sys-color-primary-container, rgba(26, 115, 232, 0.12)) 85%,
135→        transparent 100%
136→      );
137→    }
138→
139→    .${PEEK_BAR_CLASS}:hover::after {
140→      opacity: 1;
141→      width: 60px;
142→    }
143→
144→    /* Tooltip for peek bar */
145→    .${PEEK_BAR_CLASS}[data-tooltip]::before {
146→      content: attr(data-tooltip);
147→      position: absolute;
148→      bottom: 100%;
149→      left: 50%;
150→      transform: translateX(-50%) translateY(-4px);
151→      padding: 6px 12px;
152→      background: var(--gm3-sys-color-inverse-surface, #303030);
153→      color: var(--gm3-sys-color-inverse-on-surface, #f5f5f5);
154→      font-family: 'Google Sans', Roboto, sans-serif;
155→      font-size: 12px;
156→      font-weight: 500;
157→      border-radius: 8px;
158→      white-space: nowrap;
159→      opacity: 0;
160→      pointer-events: none;
161→      transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
162→      z-index: 1000;
163→    }
164→
165→    .${PEEK_BAR_CLASS}:hover[data-tooltip]::before {
166→      opacity: 1;
167→      transform: translateX(-50%) translateY(-8px);
168→    }
169→
170→    /* Show peek bar when recents is hidden */
171→    .${PEEK_BAR_CLASS}.gv-visible {
172→      display: block;
173→    }
174→
175→    /* Dark mode adjustments */
176→    @media (prefers-color-scheme: dark) {
177→      .${TOGGLE_BTN_CLASS} {
178→        background: rgba(255, 255, 255, 0.08);
179→        color: #e8eaed;
180→      }
181→      .${TOGGLE_BTN_CLASS}:hover {
182→        background: rgba(255, 255, 255, 0.14);
183→      }
184→      .${PEEK_BAR_CLASS} {
185→        background: linear-gradient(
186→          90deg,
187→          transparent 0%,
188→          rgba(255, 255, 255, 0.06) 20%,
189→          rgba(255, 255, 255, 0.06) 80%,
190→          transparent 100%
191→        );
192→      }
193→      .${PEEK_BAR_CLASS}:hover {
194→        background: linear-gradient(
195→          90deg,
196→          transparent 0%,
197→          rgba(138, 180, 248, 0.15) 15%,
198→          rgba(138, 180, 248, 0.15) 85%,
199→          transparent 100%
200→        );
201→      }
202→      .${PEEK_BAR_CLASS}::after {
203→        background: #8ab4f8;
204→      }
205→    }
206→
207→    /* Explicit dark theme support */
208→    body[data-theme="dark"] .${TOGGLE_BTN_CLASS},
209→    body.dark-theme .${TOGGLE_BTN_CLASS} {
210→      background: rgba(255, 255, 255, 0.08);
211→      color: #e8eaed;
212→    }
213→    body[data-theme="dark"] .${TOGGLE_BTN_CLASS}:hover,
214→    body.dark-theme .${TOGGLE_BTN_CLASS}:hover {
215→      background: rgba(255, 255, 255, 0.14);
216→    }
217→  `;
218→  document.head.appendChild(style);
219→}
220→
221→/**
222→ * Create the toggle button element
223→ */
224→function createToggleButton(): HTMLButtonElement {
225→  const btn = document.createElement('button');
226→  btn.className = TOGGLE_BTN_CLASS;
227→  btn.setAttribute('aria-label', getTranslationSync('recentsHide') || 'Hide recent items');
228→  btn.title = getTranslationSync('recentsHide') || 'Hide recent items';
229→
230→  // Eye-off icon (Material Symbols)
231→  btn.innerHTML = `
232→    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 -960 960 960" fill="currentColor">
233→      <path d="m644-428-58-58q9-47-27-88t-93-32l-58-58q17-8 34.5-12t37.5-4q75 0 127.5 52.5T660-500q0 20-4 37.5T644-428Zm128 126-58-56q38-29 67.5-63.5T832-500q-50-101-143.5-160.5T480-720q-29 0-57 4t-55 12l-62-62q41-17 84-25.5t90-8.5q151 0 269 83.5T920-500q-23 59-60.5 109.5T772-302Zm20 246L624-222q-35 11-70.5 16.5T480-200q-151 0-269-83.5T40-500q21-53 53-98.5t73-81.5L56-792l56-56 736 736-56 56ZM222-624q-29 26-53 57t-41 67q50 101 143.5 160.5T480-280q20 0 39-2.5t39-5.5l-36-38q-11 3-21 4.5t-21 1.5q-75 0-127.5-52.5T300-500q0-11 1.5-21t4.5-21l-84-82Zm319 93Zm-151 75Z"/>
234→    </svg>
235→  `;
236→
237→  return btn;
238→}
239→
240→/**
241→ * Create the peek bar element for restoring hidden section
242→ */
243→function createPeekBar(): HTMLDivElement {
244→  const bar = document.createElement('div');
245→  bar.className = PEEK_BAR_CLASS;
246→  bar.setAttribute('data-tooltip', getTranslationSync('recentsShow') || 'Show recent items');
247→  bar.setAttribute('role', 'button');
248→  bar.setAttribute('tabindex', '0');
249→  bar.setAttribute('aria-label', getTranslationSync('recentsShow') || 'Show recent items');
250→
251→  return bar;
252→}
253→
254→/**
255→ * Get the current hidden state from storage
256→ */
257→async function getHiddenState(): Promise<boolean> {
258→  return new Promise((resolve) => {
259→    try {
260→      chrome.storage?.local?.get({ [STORAGE_KEY]: false }, (result) => {
261→        resolve(result?.[STORAGE_KEY] === true);
262→      });
263→    } catch {
264→      // Fallback to localStorage
265→      resolve(localStorage.getItem(STORAGE_KEY) === 'true');
266→    }
267→  });
268→}
269→
270→/**
271→ * Save the hidden state to storage
272→ */
273→async function setHiddenState(hidden: boolean): Promise<void> {
274→  return new Promise((resolve) => {
275→    try {
276→      chrome.storage?.local?.set({ [STORAGE_KEY]: hidden }, () => resolve());
277→    } catch {
278→      // Fallback to localStorage
279→      localStorage.setItem(STORAGE_KEY, String(hidden));
280→      resolve();
281→    }
282→  });
283→}
284→
285→/**
286→ * Apply the hidden/visible state to the recents section
287→ */
288→function applyState(recentsEl: HTMLElement, peekBar: HTMLDivElement, hidden: boolean): void {
289→  if (hidden) {
290→    recentsEl.classList.add(HIDDEN_CLASS);
291→    peekBar.classList.add('gv-visible');
292→  } else {
293→    recentsEl.classList.remove(HIDDEN_CLASS);
294→    peekBar.classList.remove('gv-visible');
295→  }
296→}
297→
298→/**
299→ * Setup the hide/show functionality for a recents element
300→ */
301→async function setupRecentsHider(recentsEl: HTMLElement): Promise<void> {
302→  // Check if already processed
303→  if (recentsEl.querySelector(`.${TOGGLE_BTN_CLASS}`)) return;
304→
305→  // Create UI elements
306→  const toggleBtn = createToggleButton();
307→  const peekBar = createPeekBar();
308→
309→  // Insert elements
310→  recentsEl.appendChild(toggleBtn);
311→  recentsEl.parentElement?.insertBefore(peekBar, recentsEl.nextSibling);
312→
313→  // Get initial state and apply
314→  const isHidden = await getHiddenState();
315→  applyState(recentsEl, peekBar, isHidden);
316→
317→  // Toggle button click handler
318→  toggleBtn.addEventListener('click', async (e) => {
319→    e.stopPropagation();
320→    e.preventDefault();
321→
322→    await setHiddenState(true);
323→    applyState(recentsEl, peekBar, true);
324→  });
325→
326→  // Peek bar click handler
327→  peekBar.addEventListener('click', async () => {
328→    await setHiddenState(false);
329→    applyState(recentsEl, peekBar, false);
330→  });
331→
332→  // Keyboard support for peek bar
333→  peekBar.addEventListener('keydown', async (e) => {
334→    if (e.key === 'Enter' || e.key === ' ') {
335→      e.preventDefault();
336→      await setHiddenState(false);
337→      applyState(recentsEl, peekBar, false);
338→    }
339→  });
340→}
341→
342→/**
343→ * Update UI text when language changes
344→ */
345→function updateLanguageText(): void {
346→  // Update toggle buttons
347→  document.querySelectorAll<HTMLButtonElement>(`.${TOGGLE_BTN_CLASS}`).forEach((btn) => {
348→    const text = getTranslationSync('recentsHide') || 'Hide recent items';
349→    btn.setAttribute('aria-label', text);
350→    btn.title = text;
351→  });
352→
353→  // Update peek bars
354→  document.querySelectorAll<HTMLDivElement>(`.${PEEK_BAR_CLASS}`).forEach((bar) => {
355→    const text = getTranslationSync('recentsShow') || 'Show recent items';
356→    bar.setAttribute('data-tooltip', text);
357→    bar.setAttribute('aria-label', text);
358→  });
359→}
360→
361→/**
362→ * Initialize the recents hider
363→ */
364→function initRecentsHider(): void {
365→  if (initialized) return;
366→  initialized = true;
367→
368→  injectStyles();
369→
370→  // Setup existing recents elements
371→  const recentsEls = document.querySelectorAll<HTMLElement>(RECENTS_SELECTOR);
372→  recentsEls.forEach((el) => setupRecentsHider(el));
373→
374→  // Observe for dynamically added recents elements (SPA navigation)
375→  observer = new MutationObserver((mutations) => {
376→    for (const mutation of mutations) {
377→      for (const node of Array.from(mutation.addedNodes)) {
378→        if (node instanceof HTMLElement) {
379→          if (node.matches(RECENTS_SELECTOR)) {
380→            setupRecentsHider(node);
381→          }
382→          // Also check children
383→          const children = node.querySelectorAll<HTMLElement>(RECENTS_SELECTOR);
384→          children.forEach((el) => setupRecentsHider(el));
385→        }
386→      }
387→    }
388→  });
389→
390→  observer.observe(document.body, { childList: true, subtree: true });
391→
392→  // Listen for language changes and update UI text
393→  browser.storage.onChanged.addListener((changes, areaName) => {
394→    if ((areaName === 'sync' || areaName === 'local') && changes[StorageKeys.LANGUAGE]) {
395→      updateLanguageText();
396→    }
397→  });
398→}
399→
400→/**
401→ * Cleanup function
402→ */
403→function cleanup(): void {
404→  if (observer) {
405→    observer.disconnect();
406→    observer = null;
407→  }
408→
409→  // Remove styles
410→  document.getElementById(STYLE_ID)?.remove();
411→
412→  // Remove added elements
413→  document.querySelectorAll(`.${TOGGLE_BTN_CLASS}`).forEach((el) => el.remove());
414→  document.querySelectorAll(`.${PEEK_BAR_CLASS}`).forEach((el) => el.remove());
415→  document.querySelectorAll(`.${HIDDEN_CLASS}`).forEach((el) => {
416→    el.classList.remove(HIDDEN_CLASS);
417→  });
418→
419→  initialized = false;
420→}
421→
422→/**
423→ * Start the recents hider feature
424→ */
425→export function startRecentsHider(): () => void {
426→  // Only run on gemini.google.com
427→  if (location.hostname !== 'gemini.google.com') {
428→    return () => {};
429→  }
430→
431→  // Wait for DOM to be ready
432→  if (document.readyState === 'loading') {
433→    document.addEventListener('DOMContentLoaded', initRecentsHider);
434→  } else {
435→    // Small delay to ensure Gemini's UI is rendered
436→    setTimeout(initRecentsHider, 500);
437→  }
