import DOMPurify from 'dompurify';
import { marked } from 'marked';

import { StorageKeys } from '@/core/types/common';
import { EXTENSION_VERSION } from '@/core/utils/version';
import { getCurrentLanguage, getTranslationSync } from '@/utils/i18n';
import type { AppLanguage } from '@/utils/language';

/**
 * Dynamically import all markdown changelog files.
 * Keyed by relative path, e.g. './notes/1.2.8.md'
 */
const changelogModules = import.meta.glob('./notes/*.md', {
  query: '?raw',
  import: 'default',
  eager: false,
}) as Record<string, () => Promise<string>>;

/**
 * Dynamically import all image assets so Vite resolves their URLs.
 */
const imageAssets = import.meta.glob('./notes/assets/*', {
  eager: true,
  import: 'default',
}) as Record<string, string>;

/**
 * Build a lookup map: filename (without path) → resolved URL
 * e.g. '1.2.8-hero.gif' → '/assets/1.2.8-hero-abc123.gif'
 */
function buildImageMap(): Record<string, string> {
  const map: Record<string, string> = {};
  for (const [path, url] of Object.entries(imageAssets)) {
    const filename = path.split('/').pop();
    if (filename) {
      map[filename] = url;
    }
  }
  return map;
}

/**
 * Parse optional YAML-like front matter from the markdown.
 * Returns the images map and the remaining body.
 *
 * Format:
 * ```
 * ---
 * images:
 *   hero: ./assets/1.2.8-hero.gif
 * ---
 * ```
 */
function parseFrontMatter(raw: string): {
  imageAliases: Record<string, string>;
  body: string;
} {
  const match = raw.match(/^---\r?\n([\s\S]*?)\r?\n---\r?\n([\s\S]*)$/);
  if (!match) {
    return { imageAliases: {}, body: raw };
  }

  const frontMatter = match[1];
  const body = match[2];
  const imageAliases: Record<string, string> = {};

  // Simple YAML parser for the images block
  let inImages = false;
  for (const line of frontMatter.split('\n')) {
    const trimmed = line.trim();
    if (trimmed === 'images:') {
      inImages = true;
      continue;
    }
    if (inImages && trimmed.includes(':')) {
      const colonIdx = trimmed.indexOf(':');
      const key = trimmed.slice(0, colonIdx).trim();
      let value = trimmed.slice(colonIdx + 1).trim();
      // Extract just the filename from paths like ./assets/1.2.8-hero.gif
      const filename = value.split('/').pop();
      if (key && filename) {
        value = filename;
        imageAliases[key] = value;
      }
    } else if (inImages && !trimmed.startsWith(' ') && trimmed.length > 0) {
      inImages = false;
    }
  }

  return { imageAliases, body };
}

/**
 * Extract the section matching the user's language from a multi-language
 * markdown file. Falls back to 'en' if the requested language is missing.
 */
export function extractLocalizedContent(raw: string, lang: AppLanguage): string {
  const { imageAliases, body } = parseFrontMatter(raw);

  // Split by <!-- lang:xx --> markers
  const sections = new Map<string, string>();
  const parts = body.split(/<!--\s*lang:(\w+)\s*-->/);

  // parts[0] is text before the first marker (usually empty)
  // parts[1] = lang code, parts[2] = content, parts[3] = lang code, etc.
  for (let i = 1; i < parts.length; i += 2) {
    const langCode = parts[i];
    const content = parts[i + 1]?.trim() ?? '';
    if (langCode && content) {
      sections.set(langCode, content);
    }
  }

  let content = sections.get(lang) ?? sections.get('en') ?? '';

  // Resolve image aliases: ![alt](key) → ![alt](resolved-url)
  if (Object.keys(imageAliases).length > 0) {
    const resolvedImages = buildImageMap();
    content = content.replace(
      /!\[([^\]]*)\]\(([^)]+)\)/g,
      (_match, alt: string, key: string) => {
        const filename = imageAliases[key];
        if (filename && resolvedImages[filename]) {
          return `![${alt}](${resolvedImages[filename]})`;
        }
        // Try direct filename match
        if (resolvedImages[key]) {
          return `![${alt}](${resolvedImages[key]})`;
        }
        return _match;
      },
    );
  }

  return content;
}

/**
 * Get the docs URL for the current language.
 * zh is the root locale (no prefix), others use /{locale}/ prefix.
 */
function getDocsUrl(lang: AppLanguage): string {
  const base = 'https://voyager.nagi.fun';
  const path = '/guide/getting-started';
  if (lang === 'zh') return `${base}${path}`;
  return `${base}/${lang}${path}`;
}

/**
 * Render the changelog modal DOM.
 */
function createChangelogModal(htmlContent: string, lang: AppLanguage): {
  overlay: HTMLDivElement;
  onClose: () => Promise<void>;
} {
  const overlay = document.createElement('div');
  overlay.className = 'gv-changelog-overlay';

  const dialog = document.createElement('div');
  dialog.className = 'gv-changelog-dialog';

  // Header
  const header = document.createElement('div');
  header.className = 'gv-changelog-header';

  const title = document.createElement('span');
  title.className = 'gv-changelog-title';
  title.textContent = getTranslationSync('changelog_title');

  const version = document.createElement('span');
  version.className = 'gv-changelog-version';
  version.textContent = `v${EXTENSION_VERSION}`;

  const closeBtn = document.createElement('button');
  closeBtn.className = 'gv-changelog-close';
  closeBtn.textContent = '✕';
  closeBtn.setAttribute('aria-label', 'Close');

  header.appendChild(title);
  header.appendChild(version);
  header.appendChild(closeBtn);

  // Body
  const body = document.createElement('div');
  body.className = 'gv-changelog-body';
  body.innerHTML = htmlContent;

  // Footer
  const footer = document.createElement('div');
  footer.className = 'gv-changelog-footer';

  const docsLink = document.createElement('a');
  docsLink.className = 'gv-changelog-docs-link';
  docsLink.href = getDocsUrl(lang);
  docsLink.target = '_blank';
  docsLink.rel = 'noopener noreferrer';
  docsLink.textContent = getTranslationSync('changelog_docs_link');

  const gotItBtn = document.createElement('button');
  gotItBtn.className = 'gv-changelog-got-it';
  gotItBtn.textContent = getTranslationSync('changelog_close');

  footer.appendChild(docsLink);
  footer.appendChild(gotItBtn);

  dialog.appendChild(header);
  dialog.appendChild(body);
  dialog.appendChild(footer);
  overlay.appendChild(dialog);

  const onClose = async (): Promise<void> => {
    try {
      await chrome.storage.local.set({
        [StorageKeys.CHANGELOG_DISMISSED_VERSION]: EXTENSION_VERSION,
      });
    } catch {
      // Extension context may be invalidated
    }
    overlay.remove();
  };

  closeBtn.addEventListener('click', onClose);
  gotItBtn.addEventListener('click', onClose);
  overlay.addEventListener('click', (e) => {
    if (e.target === overlay) {
      onClose();
    }
  });

  return { overlay, onClose };
}

/**
 * Start the changelog feature.
 * Shows a version-based changelog popup when the user upgrades to a new version.
 * Returns a cleanup function.
 */
export async function startChangelog(): Promise<() => void> {
  let overlayRef: HTMLDivElement | null = null;

  try {
    // 1. Check dismissed version
    const result = await chrome.storage.local.get(StorageKeys.CHANGELOG_DISMISSED_VERSION);
    const dismissedVersion = result[StorageKeys.CHANGELOG_DISMISSED_VERSION] as string | undefined;

    if (dismissedVersion === EXTENSION_VERSION) {
      return () => {};
    }

    // 2. Try to load the changelog for the current version
    const modulePath = `./notes/${EXTENSION_VERSION}.md`;
    const loader = changelogModules[modulePath];

    if (!loader) {
      // No changelog file for this version — silently skip
      return () => {};
    }

    const rawMarkdown = await loader();

    // 3. Get current language and extract localized content
    const lang = await getCurrentLanguage();
    const localizedContent = extractLocalizedContent(rawMarkdown, lang);

    if (!localizedContent) {
      return () => {};
    }

    // 4. Convert markdown to HTML
    const rawHtml = await marked.parse(localizedContent);
    const sanitizedHtml = DOMPurify.sanitize(rawHtml, {
      ALLOWED_TAGS: [
        'h1', 'h2', 'h3', 'h4', 'h5', 'h6',
        'p', 'br', 'hr',
        'ul', 'ol', 'li',
        'strong', 'em', 'code', 'pre',
        'a', 'img',
        'blockquote',
      ],
      ALLOWED_ATTR: ['href', 'target', 'rel', 'src', 'alt', 'class'],
    });

    // 5. Inject modal
    const { overlay } = createChangelogModal(sanitizedHtml, lang);
    document.body.appendChild(overlay);
    overlayRef = overlay;
  } catch {
    // Silently fail — changelog is non-critical
  }

  return () => {
    if (overlayRef) {
      overlayRef.remove();
      overlayRef = null;
    }
  };
}

