}

.gv-pm-confirm button:hover {
  filter: brightness(1.05);
}

.gv-pm-confirm::after {
  content: '';
  position: absolute;
  width: 10px;
  height: 10px;
  background: rgba(2, 6, 23, 0.96);
  border: 1px solid rgba(255, 255, 255, 0.12);
  transform: rotate(45deg);
}

.gv-pm-confirm[data-side='left']::after {
  right: -6px;
  top: 50%;
  transform: translateY(-50%) rotate(45deg);
  border-left: none;
  border-bottom: none;
}

.gv-pm-confirm[data-side='right']::after {
  left: -6px;
  top: 50%;
  transform: translateY(-50%) rotate(45deg);
  border-right: none;
  border-top: none;
}

.gv-pm-drag {
  width: 10px;
