.gv-aistudio .gv-folder-uncategorized-content {
  padding-left: 8px;
}

.gv-aistudio .google-symbols[data-icon='inbox']::before {
  content: '📥';
  font-size: 14px;
}

.gv-aistudio .google-symbols[data-icon='more_vert']::before {
  content: '⋮';
  font-size: 16px;
}

.gv-aistudio .google-symbols[data-icon='add']::before {
  content: '+';
  font-size: 16px;
}

.gv-aistudio .google-symbols[data-icon='download']::before {
  content: '⬇';
  font-size: 16px;
}

.gv-aistudio .google-symbols[data-icon='upload']::before {
  content: '⬆';
  font-size: 16px;
}

.gv-aistudio .google-symbols[data-icon='star']::before {
  content: '★';
  font-size: 14px;
}

.gv-aistudio .google-symbols[data-icon='star_outline']::before {
  content: '☆';
  font-size: 14px;
}

.gv-aistudio .google-symbols[data-icon='close']::before {
  content: '×';
  font-size: 14px;
