  1→import DOMPurify from 'dompurify';
  2→import { marked } from 'marked';
  3→
  4→import { StorageKeys } from '@/core/types/common';
  5→import { EXTENSION_VERSION } from '@/core/utils/version';
  6→import { getCurrentLanguage, getTranslationSync } from '@/utils/i18n';
  7→import type { AppLanguage } from '@/utils/language';
  8→
  9→/**
 10→ * Dynamically import all markdown changelog files.
 11→ * Keyed by relative path, e.g. './notes/1.2.8.md'
 12→ */
 13→const changelogModules = import.meta.glob('./notes/*.md', {
 14→  query: '?raw',
 15→  import: 'default',
 16→  eager: false,
 17→}) as Record<string, () => Promise<string>>;
 18→
 19→/**
 20→ * Dynamically import all image assets so Vite resolves their URLs.
 21→ */
 22→const imageAssets = import.meta.glob('./notes/assets/*', {
 23→  eager: true,
 24→  import: 'default',
 25→}) as Record<string, string>;
 26→
 27→/**
 28→ * Build a lookup map: filename (without path) → resolved URL
 29→ * e.g. '1.2.8-hero.gif' → '/assets/1.2.8-hero-abc123.gif'
 30→ */
 31→function buildImageMap(): Record<string, string> {
 32→  const map: Record<string, string> = {};
 33→  for (const [path, url] of Object.entries(imageAssets)) {
 34→    const filename = path.split('/').pop();
 35→    if (filename) {
 36→      map[filename] = url;
 37→    }
 38→  }
 39→  return map;
 40→}
 41→
 42→/**
 43→ * Parse optional YAML-like front matter from the markdown.
 44→ * Returns the images map and the remaining body.
 45→ *
 46→ * Format:
 47→ * ```
 48→ * ---
 49→ * images:
 50→ *   hero: ./assets/1.2.8-hero.gif
 51→ * ---
 52→ * ```
 53→ */
 54→function parseFrontMatter(raw: string): {
 55→  imageAliases: Record<string, string>;
 56→  body: string;
 57→} {
 58→  const match = raw.match(/^---\r?\n([\s\S]*?)\r?\n---\r?\n([\s\S]*)$/);
 59→  if (!match) {
 60→    return { imageAliases: {}, body: raw };
 61→  }
 62→
 63→  const frontMatter = match[1];
 64→  const body = match[2];
 65→  const imageAliases: Record<string, string> = {};
 66→
 67→  // Simple YAML parser for the images block
 68→  let inImages = false;
 69→  for (const line of frontMatter.split('\n')) {
 70→    const trimmed = line.trim();
 71→    if (trimmed === 'images:') {
 72→      inImages = true;
 73→      continue;
 74→    }
 75→    if (inImages && trimmed.includes(':')) {
 76→      const colonIdx = trimmed.indexOf(':');
 77→      const key = trimmed.slice(0, colonIdx).trim();
 78→      let value = trimmed.slice(colonIdx + 1).trim();
 79→      // Extract just the filename from paths like ./assets/1.2.8-hero.gif
 80→      const filename = value.split('/').pop();
 81→      if (key && filename) {
 82→        value = filename;
 83→        imageAliases[key] = value;
 84→      }
 85→    } else if (inImages && !trimmed.startsWith(' ') && trimmed.length > 0) {
 86→      inImages = false;
 87→    }
 88→  }
 89→
 90→  return { imageAliases, body };
 91→}
 92→
 93→/**
 94→ * Extract the section matching the user's language from a multi-language
 95→ * markdown file. Falls back to 'en' if the requested language is missing.
 96→ */
 97→export function extractLocalizedContent(raw: string, lang: AppLanguage): string {
 98→  const { imageAliases, body } = parseFrontMatter(raw);
 99→
100→  // Split by <!-- lang:xx --> markers
101→  const sections = new Map<string, string>();
102→  const parts = body.split(/<!--\s*lang:(\w+)\s*-->/);
103→
104→  // parts[0] is text before the first marker (usually empty)
105→  // parts[1] = lang code, parts[2] = content, parts[3] = lang code, etc.
106→  for (let i = 1; i < parts.length; i += 2) {
107→    const langCode = parts[i];
108→    const content = parts[i + 1]?.trim() ?? '';
109→    if (langCode && content) {
110→      sections.set(langCode, content);
111→    }
112→  }
113→
114→  let content = sections.get(lang) ?? sections.get('en') ?? '';
115→
116→  // Resolve image aliases: ![alt](key) → ![alt](resolved-url)
117→  if (Object.keys(imageAliases).length > 0) {
118→    const resolvedImages = buildImageMap();
119→    content = content.replace(/!\[([^\]]*)\]\(([^)]+)\)/g, (_match, alt: string, key: string) => {
120→      const filename = imageAliases[key];
121→      if (filename && resolvedImages[filename]) {
122→        return `![${alt}](${resolvedImages[filename]})`;
123→      }
124→      // Try direct filename match
125→      if (resolvedImages[key]) {
126→        return `![${alt}](${resolvedImages[key]})`;
127→      }
128→      return _match;
129→    });
130→  }
131→
132→  return content;
133→}
134→
135→/**
136→ * Get the docs URL for the current language.
137→ * zh is the root locale (no prefix), others use /{locale}/ prefix.
138→ */
139→function getDocsUrl(lang: AppLanguage): string {
140→  const base = 'https://voyager.nagi.fun';
141→  const path = '/guide/getting-started';
142→  if (lang === 'zh') return `${base}${path}`;
143→  return `${base}/${lang}${path}`;
144→}
145→
146→/**
147→ * Render the changelog modal DOM.
148→ */
149→function createChangelogModal(
150→  htmlContent: string,
151→  lang: AppLanguage,
152→): {
153→  overlay: HTMLDivElement;
154→  onClose: () => Promise<void>;
155→} {
156→  const overlay = document.createElement('div');
157→  overlay.className = 'gv-changelog-overlay';
158→
159→  const dialog = document.createElement('div');
160→  dialog.className = 'gv-changelog-dialog';
161→
162→  // Header
163→  const header = document.createElement('div');
164→  header.className = 'gv-changelog-header';
165→
166→  const title = document.createElement('span');
167→  title.className = 'gv-changelog-title';
168→  title.textContent = getTranslationSync('changelog_title');
169→
170→  const version = document.createElement('span');
171→  version.className = 'gv-changelog-version';
172→  version.textContent = `v${EXTENSION_VERSION}`;
173→
174→  const closeBtn = document.createElement('button');
175→  closeBtn.className = 'gv-changelog-close';
176→  closeBtn.textContent = '✕';
177→  closeBtn.setAttribute('aria-label', 'Close');
178→
179→  header.appendChild(title);
180→  header.appendChild(version);
181→  header.appendChild(closeBtn);
182→
183→  // Body
184→  const body = document.createElement('div');
185→  body.className = 'gv-changelog-body';
186→  body.innerHTML = htmlContent;
187→
188→  // Footer
189→  const footer = document.createElement('div');
190→  footer.className = 'gv-changelog-footer';
191→
192→  const docsLink = document.createElement('a');
193→  docsLink.className = 'gv-changelog-docs-link';
194→  docsLink.href = getDocsUrl(lang);
195→  docsLink.target = '_blank';
196→  docsLink.rel = 'noopener noreferrer';
197→  docsLink.textContent = getTranslationSync('changelog_docs_link');
198→
199→  const gotItBtn = document.createElement('button');
200→  gotItBtn.className = 'gv-changelog-got-it';
201→  gotItBtn.textContent = getTranslationSync('changelog_close');
202→
203→  footer.appendChild(docsLink);
204→  footer.appendChild(gotItBtn);
205→
206→  dialog.appendChild(header);
207→  dialog.appendChild(body);
208→  dialog.appendChild(footer);
209→  overlay.appendChild(dialog);
210→
211→  const onClose = async (): Promise<void> => {
212→    try {
213→      await chrome.storage.local.set({
214→        [StorageKeys.CHANGELOG_DISMISSED_VERSION]: EXTENSION_VERSION,
215→      });
216→    } catch {
217→      // Extension context may be invalidated
218→    }
219→    overlay.remove();
220→  };
221→
222→  closeBtn.addEventListener('click', onClose);
223→  gotItBtn.addEventListener('click', onClose);
224→  overlay.addEventListener('click', (e) => {
225→    if (e.target === overlay) {
226→      onClose();
227→    }
228→  });
229→
230→  return { overlay, onClose };
231→}
232→
233→/**
234→ * Start the changelog feature.
235→ * Shows a version-based changelog popup when the user upgrades to a new version.
236→ * Returns a cleanup function.
237→ */
238→export async function startChangelog(): Promise<() => void> {
239→  let overlayRef: HTMLDivElement | null = null;
240→
241→  try {
242→    // 1. Check dismissed version
243→    const result = await chrome.storage.local.get(StorageKeys.CHANGELOG_DISMISSED_VERSION);
244→    const dismissedVersion = result[StorageKeys.CHANGELOG_DISMISSED_VERSION] as string | undefined;
245→
246→    if (dismissedVersion === EXTENSION_VERSION) {
247→      return () => {};
248→    }
249→
250→    // 2. Try to load the changelog for the current version
251→    const modulePath = `./notes/${EXTENSION_VERSION}.md`;
252→    const loader = changelogModules[modulePath];
253→
254→    if (!loader) {
255→      // No changelog file for this version — silently skip
256→      return () => {};
257→    }
258→
259→    const rawMarkdown = await loader();
260→
261→    // 3. Get current language and extract localized content
262→    const lang = await getCurrentLanguage();
263→    const localizedContent = extractLocalizedContent(rawMarkdown, lang);
264→
265→    if (!localizedContent) {
266→      return () => {};
267→    }
268→
269→    // 4. Convert markdown to HTML
270→    const rawHtml = await marked.parse(localizedContent);
271→    const sanitizedHtml = DOMPurify.sanitize(rawHtml, {
272→      ALLOWED_TAGS: [
273→        'h1',
274→        'h2',
275→        'h3',
276→        'h4',
277→        'h5',
278→        'h6',
279→        'p',
280→        'br',
281→        'hr',
282→        'ul',
283→        'ol',
284→        'li',
285→        'strong',
286→        'em',
287→        'code',
288→        'pre',
289→        'a',
290→        'img',
291→        'blockquote',
292→      ],
293→      ALLOWED_ATTR: ['href', 'target', 'rel', 'src', 'alt', 'class'],
294→    });
295→
296→    // 5. Inject modal
297→    const { overlay } = createChangelogModal(sanitizedHtml, lang);
298→    document.body.appendChild(overlay);
299→    overlayRef = overlay;
300→  } catch {
301→    // Silently fail — changelog is non-critical
302→  }
303→
304→  return () => {
305→    if (overlayRef) {
306→      overlayRef.remove();
307→      overlayRef = null;
308→    }
309→  };
310→}
311→

