  // parts[1] = lang code, parts[2] = content, parts[3] = lang code, etc.
  for (let i = 1; i < parts.length; i += 2) {
    const langCode = parts[i];
    const content = parts[i + 1]?.trim() ?? '';
    if (langCode && content) {
      sections.set(langCode, content);
    }
  }

  let content = sections.get(lang) ?? sections.get('en') ?? '';

  // Resolve image aliases: ![alt](key) → ![alt](resolved-url)
  if (Object.keys(imageAliases).length > 0) {
    const resolvedImages = buildImageMap();
    content = content.replace(/!\[([^\]]*)\]\(([^)]+)\)/g, (_match, alt: string, key: string) => {
      const filename = imageAliases[key];
      if (filename && resolvedImages[filename]) {
        return `![${alt}](${resolvedImages[filename]})`;
      }
      // Try direct filename match
      if (resolvedImages[key]) {
        return `![${alt}](${resolvedImages[key]})`;
      }
      return _match;
    });
  }

  return content;
}

/**
 * Get the docs URL for the current language.
