/* Gemini-like timeline styling with CSS variables and theme awareness */
:root {
  --timeline-dot-color: #94a3b8;
  --timeline-dot-active-color: #3b82f6;
  --timeline-star-color: #f59e0b;
  --timeline-tooltip-bg: #ffffff;
  --timeline-tooltip-text: #0f172a;
  --timeline-tooltip-border: #e2e8f0;
  --timeline-tooltip-radius: 12px;
  --timeline-tooltip-shadow: 0 8px 28px rgba(2, 8, 23, 0.16), 0 2px 6px rgba(2, 8, 23, 0.08);
  --timeline-tooltip-lh: 18px;
  --timeline-tooltip-pad-y: 10px;
  --timeline-tooltip-pad-x: 12px;
  --timeline-tooltip-border-w: 1px;
  --timeline-tooltip-arrow-size: 8px;
  --timeline-tooltip-arrow-outside: 4px;
  --timeline-tooltip-anim-in: 140ms cubic-bezier(0.2, 0.8, 0.2, 1);
  --timeline-tooltip-anim-out: 100ms linear;
  --timeline-bar-bg: rgba(248, 250, 252, 0.85);
  --timeline-dot-size: 12px;
  --timeline-active-ring: 3px;
  --timeline-track-padding: 16px;
  --timeline-tooltip-max: 288px;
  --timeline-min-gap: 24px;
  --timeline-hit-size: 30px;
  --timeline-tooltip-gap-visual: 8px;
  --timeline-tooltip-gap-box: 4px;
  --timeline-hold-ms: 550ms;
}

@media (prefers-color-scheme: dark) {
  :root {
    --timeline-dot-color: #475569;
    --timeline-dot-active-color: #60a5fa;
    --timeline-star-color: #f59e0b;
    --timeline-tooltip-bg: #0b1220;
    --timeline-tooltip-text: #e2e8f0;
    --timeline-tooltip-border: #1f2937;
    --timeline-bar-bg: rgba(2, 6, 23, 0.72);
  }
}

/* Gemini theme support - Overrides system preferences on Gemini site */
.theme-host.dark-theme {
  --timeline-dot-color: #475569;
  --timeline-dot-active-color: #60a5fa;
  --timeline-star-color: #f59e0b;
  --timeline-tooltip-bg: #0b1220;
  --timeline-tooltip-text: #e2e8f0;
  --timeline-tooltip-border: #1f2937;
  --timeline-bar-bg: rgba(2, 6, 23, 0.72);
}

.theme-host.light-theme {
  --timeline-dot-color: #94a3b8;
  --timeline-dot-active-color: #3b82f6;
  --timeline-star-color: #f59e0b;
  --timeline-tooltip-bg: #ffffff;
  --timeline-tooltip-text: #0f172a;
  --timeline-tooltip-border: #e2e8f0;
  --timeline-bar-bg: rgba(248, 250, 252, 0.85);
}

/* Gemini theme hosts */
.theme-host.dark-theme .gemini-timeline-bar:not(.timeline-no-container) {
  background-color: rgba(2, 6, 23, 0.72);
  backdrop-filter: blur(4px);
  -webkit-backdrop-filter: blur(4px);
}

.theme-host.light-theme .gemini-timeline-bar:not(.timeline-no-container) {
  background-color: rgba(248, 250, 252, 0.85);
  backdrop-filter: blur(4px);
  -webkit-backdrop-filter: blur(4px);
}

.gemini-timeline-bar {
  position: fixed;
  top: 60px;
  right: 15px;
  width: 24px;
  height: calc(100vh - 100px);
  z-index: 2147483646;
  display: flex;
  flex-direction: column;
  align-items: center;
  border-radius: 10px;
  background-color: var(--timeline-bar-bg);
  backdrop-filter: blur(4px);
  -webkit-backdrop-filter: blur(4px);
  transition: background-color 0.3s ease;
  overflow: visible;
  contain: layout;
}

.timeline-track {
  position: relative;
  width: 100%;
  height: 100%;
  overflow-y: auto;
  overflow-x: visible;
  background: transparent;
  padding-left: 2px;
  padding-right: 2px;
}

.timeline-track-content {
  position: relative;
  width: 100%;
  height: 100%;
}

.timeline-dot {
  position: absolute;
  left: 50%;
  top: calc(
    var(--timeline-track-padding) + (100% - 2 * var(--timeline-track-padding)) * var(--n, 0)
  );
  transform: translate(-50%, -50%);
  width: var(--timeline-hit-size);
  height: var(--timeline-hit-size);
  background: transparent;
  border: none;
  cursor: pointer;
  padding: 0;
}

.timeline-dot::after {
  content: '';
  position: absolute;
  left: 50%;
  top: 50%;
  width: var(--timeline-dot-size);
  height: var(--timeline-dot-size);
  transform: translate(-50%, -50%);
  border-radius: 50%;
  background-color: var(--timeline-dot-color);
  transition:
    transform 0.15s ease,
    box-shadow 0.15s ease;
}

/* true punch-through: remove filled dot and draw only inner stroke to make host page visible */
html.dark .timeline-track,
[data-theme='dark'] .timeline-track,
[data-color-scheme='dark'] .timeline-track {
  background: transparent;
}

html.dark .timeline-dot:not(.active):not(.starred)::after,
[data-theme='dark'] .timeline-dot:not(.active):not(.starred)::after,
[data-color-scheme='dark'] .timeline-dot:not(.active):not(.starred)::after {
  background: #000;
  box-shadow: none;
}

.timeline-dot:hover::after {
  transform: translate(-50%, -50%) scale(1.15);
}

.timeline-dot:focus-visible::after {
  box-shadow: 0 0 6px var(--timeline-dot-active-color);
}

.timeline-dot.active::after {
  box-shadow:
    0 0 0 var(--timeline-active-ring) var(--timeline-dot-active-color),
    0 0 10px rgba(59, 130, 246, 0.45);
}

.timeline-dot.starred::after {
  background-color: var(--timeline-star-color);
}

/* ===== Timeline Marker Levels (shape-based: circle, triangle, square) ===== */
.timeline-dot[data-level='1']::after {
  width: var(--timeline-dot-size);
  height: var(--timeline-dot-size);
  border-radius: 50%;
  clip-path: none;
}

.timeline-dot[data-level='2']::after {
  width: var(--timeline-dot-size);
  height: var(--timeline-dot-size);
  border-radius: 0;
  clip-path: none;
}

.timeline-dot[data-level='3']::after {
  width: var(--timeline-dot-size);
  height: var(--timeline-dot-size);
  border-radius: 0;
  clip-path: polygon(50% 0%, 0% 100%, 100% 100%);
}

/* ===== Timeline Context Menu ===== */
.timeline-context-menu {
  position: fixed;
  background-color: var(--timeline-tooltip-bg);
  border: 1px solid var(--timeline-tooltip-border);
  border-radius: 8px;
  box-shadow: var(--timeline-tooltip-shadow);
  padding: 4px 0;
  z-index: 2147483647;
  min-width: 160px;
  animation: timeline-menu-in 0.12s ease-out;
}

@keyframes timeline-menu-in {
  from {
    opacity: 0;
    transform: scale(0.95);
  }

  to {
    opacity: 1;
    transform: scale(1);
  }
}

.timeline-context-menu-title {
  padding: 8px 12px 4px 12px;
  font-size: 11px;
  font-weight: 600;
  color: var(--timeline-tooltip-text);
  opacity: 0.6;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  border-bottom: 1px solid var(--timeline-tooltip-border);
  margin-bottom: 4px;
}

.timeline-context-menu-item {
  display: flex;
  align-items: center;
  gap: 10px;
  width: 100%;
  padding: 8px 12px;
  border: none;
  background: transparent;
  color: var(--timeline-tooltip-text);
  font-size: 13px;
  cursor: pointer;
  text-align: left;
  transition: background-color 0.1s ease;
}

.timeline-context-menu-item:hover {
  background-color: var(--timeline-dot-active-color);
}

.timeline-context-menu-item.active {
  background-color: var(--timeline-dot-active-color);
  font-weight: 500;
}

.timeline-context-menu-item .level-indicator {
  width: 16px;
  height: 16px;
  display: flex;
  align-items: center;
  justify-content: center;
}

.timeline-context-menu-item .level-dot {
  width: 10px;
  height: 10px;
  background-color: var(--timeline-dot-color);
}

/* Level 1: Circle */
.timeline-context-menu-item[data-level='1'] .level-dot {
  border-radius: 50%;
  clip-path: none;
}

/* Level 2: Square */
.timeline-context-menu-item[data-level='2'] .level-dot {
  border-radius: 0;
  clip-path: none;
}

/* Level 3: Triangle */
.timeline-context-menu-item[data-level='3'] .level-dot {
  border-radius: 0;
  clip-path: polygon(50% 0%, 0% 100%, 100% 100%);
}

.timeline-context-menu-item .check-icon {
  margin-left: auto;
  color: var(--timeline-dot-active-color);
  font-size: 14px;
}

/* Context menu separator */
.timeline-context-menu-separator {
  height: 1px;
  background-color: var(--timeline-tooltip-border);
  margin: 4px 0;
}

/* Collapse menu item */
.timeline-context-menu-item.collapse-item {
  margin-top: 2px;
}

.timeline-context-menu-item .collapse-icon {
  font-size: 12px;
  color: var(--timeline-dot-color);
  transition: transform 0.2s ease;
}

/* Collapsed dot style - visual indicator */
.timeline-dot.collapsed::before {
  content: '';
  position: absolute;
  left: 50%;
  top: 50%;
  width: calc(var(--timeline-dot-size) + 10px);
  height: calc(var(--timeline-dot-size) + 10px);
  transform: translate(-50%, -50%);
  border-radius: 50%;
  border: 2px dashed var(--timeline-dot-active-color);
  opacity: 0.5;
  pointer-events: none;
}

.timeline-dot.holding::before {
  content: '';
  position: absolute;
  left: 50%;
  top: 50%;
  width: calc(var(--timeline-dot-size) + 8px);
  height: calc(var(--timeline-dot-size) + 8px);
  transform: translate(-50%, -50%);
  border-radius: 50%;
  box-shadow: 0 0 0 2px var(--timeline-dot-active-color) inset;
  opacity: 0.15;
  animation: timeline-hold-fade var(--timeline-hold-ms) linear forwards;
  pointer-events: none;
}

@keyframes timeline-hold-fade {
  from {
    opacity: 0.15;
  }

  to {
    opacity: 0.85;
  }
}

.timeline-tooltip {
  position: fixed;
  max-width: var(--timeline-tooltip-max);
  background-color: var(--timeline-tooltip-bg);
  color: var(--timeline-tooltip-text);
  padding: var(--timeline-tooltip-pad-y) var(--timeline-tooltip-pad-x);
  border-radius: var(--timeline-tooltip-radius);
  border: var(--timeline-tooltip-border-w) solid var(--timeline-tooltip-border);
  font-size: 13px;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Helvetica, Arial, sans-serif;
  line-height: var(--timeline-tooltip-lh);
  max-height: calc(
    3 * var(--timeline-tooltip-lh) + 2 * var(--timeline-tooltip-pad-y) + 2 *
      var(--timeline-tooltip-border-w)
  );
  box-shadow: var(--timeline-tooltip-shadow);
  pointer-events: none;
  z-index: 2147483647;
  display: -webkit-box;
  -webkit-line-clamp: 3;
  line-clamp: 3;
  -webkit-box-orient: vertical;
  overflow: hidden;
  word-break: break-word;
  opacity: 0;
  will-change: opacity, transform;
  transition: opacity var(--timeline-tooltip-anim-in);
}

.timeline-tooltip.visible {
  opacity: 1;
  animation: timeline-tooltip-in var(--timeline-tooltip-anim-in);
}

@media (prefers-reduced-motion: reduce) {
  .timeline-tooltip,
  .timeline-tooltip.visible {
    transition: opacity 120ms linear;
    transform: none !important;
  }
}

.timeline-tooltip[data-placement='left'] {
  transform-origin: right center;
}

.timeline-tooltip[data-placement='right'] {
  transform-origin: left center;
}

@keyframes timeline-tooltip-in {
  from {
    transform: scale(0.96);
  }

  to {
    transform: scale(1);
  }
}

.timeline-tooltip::after {
  content: '';
  position: absolute;
  width: var(--timeline-tooltip-arrow-size);
  height: var(--timeline-tooltip-arrow-size);
  background: var(--timeline-tooltip-bg);
  border: var(--timeline-tooltip-border-w) solid var(--timeline-tooltip-border);
  transform: rotate(45deg);
}

.timeline-tooltip[data-placement='left']::after {
  right: calc(-1 * var(--timeline-tooltip-arrow-outside));
  top: 50%;
  transform: translateY(-50%) rotate(45deg);
  border-left: none;
  border-bottom: none;
}

.timeline-tooltip[data-placement='right']::after {
  left: calc(-1 * var(--timeline-tooltip-arrow-outside));
  top: 50%;
  transform: translateY(-50%) rotate(45deg);
  border-right: none;
  border-top: none;
}

.timeline-track::-webkit-scrollbar {
  width: 0;
  height: 0;
}

.timeline-track {
  scrollbar-width: none;
}

.timeline-left-slider {
  position: fixed;
  top: 0;
  width: 12px;
  height: 160px;
  opacity: 0;
  transition: opacity 180ms ease;
  z-index: 2147483646;
  pointer-events: none;
}

.timeline-left-slider.visible {
  opacity: 1;
  pointer-events: auto;
}

.timeline-left-slider::before {
  content: '';
  position: absolute;
  left: 5px;
  top: 0;
  bottom: 0;
  width: 2px;
  background: rgba(0, 0, 0, 0.08);
  border-radius: 9999px;
}

html.dark .timeline-left-slider::before {
  background: rgba(255, 255, 255, 0.1);
}

.timeline-left-handle {
  position: absolute;
  left: 2px;
  width: 8px;
  height: 22px;
  background: rgba(16, 163, 127, 0.28);
  border-radius: 9999px;
  box-shadow: 0 1px 2px rgba(0, 0, 0, 0.15);
  pointer-events: auto;
  cursor: grab;
  transition: background-color 120ms ease;
}

.timeline-left-handle:hover {
  background: rgba(16, 163, 127, 0.45);
}

.timeline-left-handle:active {
  cursor: grabbing;
}

.timeline-runner-ring {
  will-change: top, opacity;
}

.gemini-timeline-bar.timeline-no-container {
  background-color: transparent !important;
  backdrop-filter: none !important;
  -webkit-backdrop-filter: none !important;
  box-shadow: none;
  contain: none;
}

/* ── Timeline Preview Panel ── */

.timeline-preview-toggle {
  position: fixed;
  width: 24px;
  height: 24px;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  background: var(--timeline-bar-bg);
  border: 1px solid var(--timeline-tooltip-border);
  cursor: pointer;
  padding: 0;
  color: var(--timeline-dot-color);
  border-radius: 6px;
  transition:
    color 0.15s ease,
    background-color 0.15s ease,
    opacity 0.15s ease;
  z-index: 2147483646;
  opacity: 0;
}
.gemini-timeline-bar:hover ~ .timeline-preview-toggle,
.timeline-preview-toggle:hover,
.timeline-preview-toggle.active {
  opacity: 1;
}
.timeline-preview-toggle:hover {
  color: var(--timeline-dot-active-color);
  background-color: var(--timeline-tooltip-bg);
}
.timeline-preview-toggle.active {
  color: var(--timeline-dot-active-color);
  background-color: var(--timeline-tooltip-bg);
}

.timeline-preview-panel {
  position: fixed;
  width: 320px;
  background-color: var(--timeline-tooltip-bg);
  border: 1px solid var(--timeline-tooltip-border);
  border-radius: var(--timeline-tooltip-radius);
  box-shadow: var(--timeline-tooltip-shadow);
  z-index: 2147483645;
  display: flex;
  flex-direction: column;
  opacity: 0;
  transform: scale(0.96) translateX(8px);
  transition:
    opacity var(--timeline-tooltip-anim-in),
    transform var(--timeline-tooltip-anim-in);
  pointer-events: none;
  overflow: hidden;
}
.timeline-preview-panel.visible {
  opacity: 1;
  transform: scale(1) translateX(0);
  pointer-events: auto;
}

.timeline-preview-search {
  padding: 8px 10px;
  border-bottom: 1px solid var(--timeline-tooltip-border);
  flex-shrink: 0;
}
.timeline-preview-search input {
  width: 100%;
  box-sizing: border-box;
  border: 1px solid var(--timeline-tooltip-border);
  border-radius: 6px;
  padding: 5px 8px;
  font-size: 13px;
  line-height: 1.4;
  background: transparent;
  color: var(--timeline-tooltip-text);
  outline: none;
  transition: border-color 0.15s ease;
}
.timeline-preview-search input::placeholder {
  color: var(--timeline-dot-color);
  opacity: 0.7;
}
.timeline-preview-search input:focus {
  border-color: var(--timeline-dot-active-color);
}

.timeline-preview-list {
  flex: 1;
  overflow-y: auto;
  overscroll-behavior: contain;
  padding: 4px 0;
  max-height: calc(100% - 42px);
}

.timeline-preview-item {
  padding: 6px 10px 6px 6px;
  cursor: pointer;
  color: var(--timeline-tooltip-text);
  font-size: 13px;
  line-height: 1.4;
  border-left: 3px solid transparent;
  display: flex;
  align-items: baseline;
  gap: 0px;
  transition: background-color 0.1s ease;
}
.timeline-preview-item:hover {
  background-color: rgba(59, 130, 246, 0.08);
}
.timeline-preview-item.active {
  border-left-color: var(--timeline-dot-active-color);
  background-color: rgba(59, 130, 246, 0.1);
}
.timeline-preview-item.starred .timeline-preview-index::after {
  content: ' \2605';
  color: var(--timeline-star-color);
  font-size: 11px;
}

.timeline-preview-index {
  flex-shrink: 0;
  font-size: 11px;
  color: var(--timeline-dot-color);
  min-width: 14px;
  text-align: left;
}

.timeline-preview-text {
