}

.theme-host.light-theme .gv-pm-gh-icon,
body.light-theme .gv-pm-gh-icon {
  background: #1f2937;
}

.theme-host.dark-theme .gv-pm-gh,
body.dark-theme .gv-pm-gh {
  background: transparent;
  border: 1px solid rgba(255, 255, 255, 0.12);
