.gv-pm-confirm::after {
  content: '';
  position: absolute;
  width: 10px;
  height: 10px;
  background: rgba(2, 6, 23, 0.96);
  border: 1px solid rgba(255, 255, 255, 0.12);
  transform: rotate(45deg);
}

.gv-pm-confirm[data-side='left']::after {
