/* ========================================
   Folder Manager Styles
   ======================================== */

/* CSS Variables for folder theming */
:root {
  --folder-bg: #ffffff;
  --folder-text: #1f2937;
  --folder-border: #e5e7eb;
  --folder-hover-bg: #f3f4f6;
  --folder-active-bg: #e0e7ff;
  --folder-icon-color: #6b7280;
  --folder-menu-bg: #ffffff;
  --folder-menu-shadow: 0 4px 12px rgba(0, 0, 0, 0.15);
  --folder-dragover-bg: #dbeafe;
  --folder-dragover-border: #3b82f6;
  /* Selection colors adapted to shadcn/ui primary (emerald/green) theme */
  --folder-selected-bg-start: #f0fdf4;
  /* green-50 - subtle background */
  --folder-selected-bg-end: #dcfce7;
  /* green-100 - slightly deeper */
  --folder-selected-border: #10b981;
  /* emerald-500 - matches primary tone */
  --folder-selected-accent: #34d399;
  /* emerald-400 - vibrant accent */
  --folder-selected-glow: rgba(16, 185, 129, 0.12);
  /* emerald-500 with low opacity */
}

/* ========================================
   Dark Theme Variable Definitions

   NOTE: Dark theme variables are defined in TWO places for different scenarios:
   1. @media (prefers-color-scheme: dark) - For system-level dark mode preference
   2. .theme-host.dark-theme - For Gemini's theme system (higher specificity)

   ⚠️ IMPORTANT: Keep these values synchronized when updating dark theme colors.
   Changes to one should be reflected in the other to ensure consistent appearance.

   SSOT (Single Source of Truth) - Subtle Selection Colors (shadcn/ui emerald):
   Light Mode:
   - Accent Bar: #34d399 (emerald-400)
   - Background Glow: rgba(16, 185, 129, 0.12) - emerald-500 at 12% opacity

   Dark Mode:
   - Accent Bar: #6ee7b7 (emerald-300)
   - Background Glow: rgba(16, 185, 129, 0.10) - emerald-500 at 10% opacity
   ======================================== */

@media (prefers-color-scheme: dark) {
  :root {
    --folder-bg: #1f2937;
    --folder-text: #e5e7eb;
    --folder-border: #374151;
    --folder-hover-bg: #374151;
    --folder-active-bg: #1e40af;
    --folder-icon-color: #9ca3af;
    --folder-menu-bg: #1f2937;
    --folder-menu-shadow: 0 4px 12px rgba(0, 0, 0, 0.5);
    --folder-dragover-bg: #1e3a8a;
    --folder-dragover-border: #60a5fa;
    /* Subtle selection colors for dark mode (shadcn/ui emerald theme) */
    --folder-selected-accent: #6ee7b7;
    /* emerald-300 - bright for dark bg */
    --folder-selected-glow: rgba(16, 185, 129, 0.1);
    /* emerald-500 at 10% opacity */
    --folder-selected-bg-start: #065f46;
    /* emerald-800 */
    --folder-selected-bg-end: #064e3b;
    /* emerald-900 */
  }
}

/* Theme support - Overrides system preferences on Gemini/AI Studio */
.theme-host.dark-theme,
body.dark-theme {
  --folder-bg: #1f2937;
  --folder-text: #e5e7eb;
  --folder-border: #374151;
  --folder-hover-bg: #374151;
  --folder-active-bg: #1e40af;
  --folder-icon-color: #9ca3af;
  --folder-menu-bg: #1f2937;
  --folder-menu-shadow: 0 4px 12px rgba(0, 0, 0, 0.5);
  --folder-dragover-bg: #1e3a8a;
  --folder-dragover-border: #60a5fa;
  /* Subtle selection colors for dark theme (shadcn/ui emerald theme) */
  --folder-selected-accent: #6ee7b7;
  /* emerald-300 - bright for dark bg */
  --folder-selected-glow: rgba(16, 185, 129, 0.1);
  /* emerald-500 at 10% opacity */
  --folder-selected-bg-start: #065f46;
  /* emerald-800 */
  --folder-selected-bg-end: #064e3b;
  /* emerald-900 */
}

.theme-host.light-theme,
body.light-theme {
  --folder-bg: #ffffff;
  --folder-text: #1f2937;
  --folder-border: #e5e7eb;
  --folder-hover-bg: #f3f4f6;
  --folder-active-bg: #e0e7ff;
  --folder-icon-color: #6b7280;
  --folder-menu-bg: #ffffff;
  --folder-menu-shadow: 0 4px 12px rgba(0, 0, 0, 0.15);
  --folder-dragover-bg: #dbeafe;
  --folder-dragover-border: #3b82f6;
  /* Subtle selection colors for light theme (shadcn/ui emerald theme) */
  --folder-selected-accent: #34d399;
  /* emerald-400 - vibrant accent */
  --folder-selected-glow: rgba(16, 185, 129, 0.12);
  /* emerald-500 at 12% opacity */
}

/* Folder Container */
.gv-folder-container {
  margin-bottom: 16px;
  padding: 8px 8px 8px 12px;
  /* Ensure alignment with Recent section */
  margin-left: 0;
}

/* Folder Header */
.gv-folder-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 8px 16px 8px 0;
  margin-bottom: 8px;
  border-radius: 8px;
  transition:
    background-color 0.2s,
    border 0.2s;
}

.gv-folder-header.gv-folder-list-dragover {
  background-color: var(--folder-dragover-bg);
  border: 2px dashed var(--folder-dragover-border);
}

.gv-folder-header .title-container {
  flex: 1;
}

/* Let native Gemini title styles take precedence - don't override */
.gv-folder-header .title {
  margin: 0;
  padding-left: 16px;
  /* Make the title darker/more subtle */
  opacity: 0.7;
  color: var(--bard-color-on-surface-variant);
}

.gv-folder-add-btn {
  width: 32px;
  height: 32px;
  border-radius: 50%;
  border: none;
  background: transparent;
  color: var(--folder-icon-color);
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition:
    background-color 0.2s,
    color 0.2s;
}

.gv-folder-add-btn:hover {
  background-color: var(--folder-hover-bg);
  color: var(--folder-text);
}

/* Folder List */
.gv-folder-list {
  display: flex;
  flex-direction: column;
  gap: 2px;
  min-height: 24px;
  /* Reduced min-height for better visual balance when empty */
  padding: 4px;
  border-radius: 8px;
  transition:
    background-color 0.2s,
    border 0.2s;
}

.gv-folder-list-dragover {
  background-color: var(--folder-dragover-bg);
  border: 2px dashed var(--folder-dragover-border);
}

/* Empty state placeholder */
.gv-folder-empty {
  padding: 4px 16px;
  font-size: 12px;
  color: var(--folder-icon-color);
  opacity: 0.7;
  user-select: none;
}

/* Folder Item */
.gv-folder-item {
  display: flex;
  flex-direction: column;
}

.gv-folder-item-header {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 8px 12px;
  border-radius: 8px;
  cursor: pointer;
  transition: background-color 0.2s;
