/* Folder Content (conversations and subfolders) */
.gv-folder-content {
  display: flex;
  flex-direction: column;
  gap: 2px;
  margin-top: 2px;
  margin-left: 12px;
  padding-left: 8px;
  border-left: 1px solid var(--folder-border);
}

/* Conversation Item in Folder */
.gv-folder-conversation {
  display: flex;
  align-items: center;
  gap: 4px;
  padding: 8px 6px;
  border-radius: 8px;
  cursor: pointer;
  transition:
    background-color 0.15s ease,
    opacity 0.2s;
  position: relative;
}

.gv-folder-conversation:hover {
  background-color: var(--folder-hover-bg);
}

/* Starred conversation - subtle gold accent on the left */
.gv-folder-conversation.gv-starred {
  background: linear-gradient(to right, rgba(251, 191, 36, 0.08) 0%, transparent 100%);
}

.gv-folder-conversation.gv-starred::before {
  content: '';
  position: absolute;
  left: 0;
  top: 50%;
  transform: translateY(-50%);
  width: 3px;
  height: 20px;
  background: linear-gradient(to bottom, #fbbf24, #f59e0b);
  border-radius: 0 2px 2px 0;
  opacity: 0.9;
}

/* ========================================
   Multi-select Selection Styles
   Subtle design inspired by starred conversations
   ======================================== */

/* Shared selection base styles - subtle like starred items */
.gv-folder-conversation-selected,
[data-test-id='conversation'].gv-conversation-selected {
  position: relative;
  /* Very subtle gradient background (similar opacity to starred: ~10%) */
  background: linear-gradient(
    to right,
    var(--folder-selected-glow) 0%,
    transparent 100%
  ) !important;
  transition: background 200ms ease;
}

/* Folder conversation specific adjustments */
.gv-folder-conversation-selected {
  border-radius: 10px;
}

/* Native conversation specific adjustments */
[data-test-id='conversation'].gv-conversation-selected {
  border-radius: 12px !important;
}

/* Subtle left accent indicator (similar to starred dot) */
.gv-folder-conversation-selected::before,
[data-test-id='conversation'].gv-conversation-selected::before {
  content: '';
  position: absolute;
  left: 0;
  top: 50%;
  transform: translateY(-50%);
  width: 3px;
  height: 60%;
  border-radius: 0 2px 2px 0;
  background-color: var(--folder-selected-accent);
  opacity: 0.9;
  pointer-events: none;
}

/* Ensure content is above the gradient overlay */
.gv-folder-conversation-selected > *,
[data-test-id='conversation'].gv-conversation-selected > * {
  position: relative;
  z-index: 1;
}

/* Invalid selection feedback animation */
.gv-invalid-selection {
  animation: invalidShake 0.4s cubic-bezier(0.36, 0.07, 0.19, 0.97);
}

@keyframes invalidShake {
  0%,
  100% {
    transform: translateX(0);
  }

  10%,
  30%,
  50%,
  70%,
  90% {
    transform: translateX(-4px);
  }

  20%,
  40%,
  60%,
  80% {
    transform: translateX(4px);
  }
}

/* Hide archived conversations (conversations that are in folders) */
[data-test-id='conversation'].gv-conversation-archived {
  display: none !important;
}

/* Multi-select mode indicator */
.gv-multi-select-indicator {
  display: none;
  align-items: center;
  justify-content: space-between;
  padding: 12px 16px;
  margin: 8px 8px 0 12px;
  /* Elegant twilight gradient background matching selection */
  background: linear-gradient(
    135deg,
    var(--folder-selected-bg-start) 0%,
    var(--folder-selected-bg-end) 100%
  );
  border: 1px solid var(--folder-selected-border);
  border-radius: 10px;
  box-shadow:
    0 4px 12px var(--folder-selected-glow),
    0 2px 4px rgba(0, 0, 0, 0.08),
    0 0 0 1px var(--folder-selected-border);
  animation: slideDown 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

@keyframes slideDown {
  from {
    opacity: 0;
    transform: translateY(-10px);
  }

  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.gv-multi-select-mode .gv-multi-select-indicator {
  display: flex;
}

.gv-multi-select-indicator-content {
  display: flex;
  align-items: center;
  gap: 8px;
}

.gv-multi-select-indicator-content mat-icon {
  font-size: 20px !important;
  width: 20px !important;
  height: 20px !important;
  color: var(--folder-selected-border);
}

.gv-multi-select-indicator-text {
  font-size: 14px;
  font-weight: 500;
  color: var(--folder-text);
}

/* Multi-select actions container */
.gv-multi-select-actions {
  display: flex;
  align-items: center;
  gap: 4px;
}

/* Common styling for action buttons */
.gv-multi-select-action-btn {
  min-width: 32px;
  width: 32px;
  height: 32px;
  border: none;
  background: transparent;
  color: var(--folder-icon-color);
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 6px;
  transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
}

.gv-multi-select-action-btn mat-icon {
  font-size: 20px !important;
  width: 20px !important;
  height: 20px !important;
}

/* Delete button specific styling */
.gv-multi-select-delete-btn {
  color: #ef4444;
}

.gv-multi-select-delete-btn:hover {
  background-color: rgba(239, 68, 68, 0.12);
  color: #dc2626;
  transform: scale(1.05);
}

.gv-multi-select-delete-btn:active {
  transform: scale(0.95);
}

/* Exit button specific styling */
.gv-multi-select-exit-btn:hover {
  background-color: rgba(0, 0, 0, 0.08);
  color: var(--folder-text);
  transform: scale(1.05);
}

.gv-multi-select-exit-btn:active {
  transform: scale(0.95);
}

/* Draggable conversations in folders */
.gv-folder-conversation[draggable='true'] {
