}

/* Star button - hidden by default, show on hover to save space */
.gv-conversation-star-btn {
  min-width: 24px;
  width: 24px;
  height: 24px;
  border: none;
  background: transparent;
  color: #9ca3af;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 6px;
  transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  flex-shrink: 0;
  padding: 0;
  opacity: 0;
}

.gv-folder-conversation:hover .gv-conversation-star-btn {
