}

.gv-conversation-star-btn:hover {
  background-color: rgba(251, 191, 36, 0.12);
  color: #fbbf24;
  transform: scale(1.05);
}

.gv-conversation-star-btn.starred:hover {
  background-color: rgba(251, 191, 36, 0.15);
}

.gv-conversation-star-btn mat-icon {
  font-size: 18px !important;
  width: 18px !important;
  height: 18px !important;
  transition: transform 0.2s;
}

.gv-conversation-star-btn:active mat-icon {
  transform: scale(0.9);
}

/* Remove button - only visible on hover */
.gv-conversation-remove-btn {
  min-width: 24px;
  width: 24px;
  height: 24px;
  border: none;
  background: transparent;
  color: #9ca3af;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 6px;
  opacity: 0;
  transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  flex-shrink: 0;
  padding: 0;
