286→/**
287→ * Start the changelog feature.
288→ * Shows a version-based changelog popup when the user upgrades to a new version.
289→ * Returns a cleanup function.
290→ */
291→export async function startChangelog(): Promise<() => void> {
292→  let overlayRef: HTMLDivElement | null = null;
293→
294→  try {
295→    // 1. Check dismissed version
296→    const result = await chrome.storage.local.get(StorageKeys.CHANGELOG_DISMISSED_VERSION);
297→    const dismissedVersion = result[StorageKeys.CHANGELOG_DISMISSED_VERSION] as string | undefined;
298→
299→    if (dismissedVersion === EXTENSION_VERSION) {
300→      return () => {};
301→    }
302→
303→    // 2. Try to load the changelog for the current version
304→    const modulePath = `./notes/${EXTENSION_VERSION}.md`;
305→    const loader = changelogModules[modulePath];
306→
307→    if (!loader) {
308→      // No changelog file for this version — silently skip
309→      return () => {};
310→    }
311→
312→    const rawMarkdown = await loader();
313→
314→    // 3. Get current language and extract localized content
315→    const lang = await getCurrentLanguage();
316→    const localizedContent = extractLocalizedContent(rawMarkdown, lang);
317→
318→    if (!localizedContent) {
319→      return () => {};
320→    }
321→
322→    // 4. Convert markdown to HTML
323→    const rawHtml = await marked.parse(localizedContent);
324→    const sanitizedHtml = DOMPurify.sanitize(rawHtml, {
325→      ALLOWED_TAGS: [
326→        'h1',
327→        'h2',
328→        'h3',
329→        'h4',
330→        'h5',
331→        'h6',
332→        'p',
333→        'br',
334→        'hr',
335→        'ul',
336→        'ol',
337→        'li',
338→        'strong',
339→        'em',
340→        'code',
341→        'pre',
342→        'a',
343→        'img',
344→        'blockquote',
345→      ],
346→      ALLOWED_ATTR: ['href', 'target', 'rel', 'src', 'alt', 'class'],
347→    });
348→
349→    // 5. Inject modal
350→    const { overlay } = createChangelogModal(sanitizedHtml, lang);
351→    document.body.appendChild(overlay);
352→    overlayRef = overlay;
353→  } catch {
354→    // Silently fail — changelog is non-critical
355→  }
356→
357→  return () => {
358→    if (overlayRef) {
359→      overlayRef.remove();
360→      overlayRef = null;
361→    }
362→  };
363→}
