import DOMPurify from 'dompurify';
import { marked } from 'marked';

import { StorageKeys } from '@/core/types/common';
import { EXTENSION_VERSION } from '@/core/utils/version';
import { getCurrentLanguage, getTranslationSync } from '@/utils/i18n';
import type { AppLanguage } from '@/utils/language';

/**
 * Dynamically import all markdown changelog files.
 * Keyed by relative path, e.g. './notes/1.2.8.md'
 */
const changelogModules = import.meta.glob('./notes/*.md', {
  query: '?raw',
  import: 'default',
  eager: false,
}) as Record<string, () => Promise<string>>;

/**
 * Dynamically import all image assets so Vite resolves their URLs.
 */
const imageAssets = import.meta.glob('./notes/assets/*', {
  eager: true,
  import: 'default',
}) as Record<string, string>;

/**
 * Build a lookup map: filename (without path) → resolved URL
 * e.g. '1.2.8-hero.gif' → '/assets/1.2.8-hero-abc123.gif'
 */
function buildImageMap(): Record<string, string> {
  const map: Record<string, string> = {};
  for (const [path, url] of Object.entries(imageAssets)) {
    const filename = path.split('/').pop();
    if (filename) {
      map[filename] = url;
    }
  }
  return map;
}

/**
