import { getCurrentLanguage, getTranslationSync } from '@/utils/i18n';
import type { AppLanguage } from '@/utils/language';

/**
 * Dynamically import all markdown changelog files.
 * Keyed by relative path, e.g. './notes/1.2.8.md'
 */
const changelogModules = import.meta.glob('./notes/*.md', {
  query: '?raw',
  import: 'default',
  eager: false,
}) as Record<string, () => Promise<string>>;

/**
 * Dynamically import all image assets so Vite resolves their URLs.
 */
const imageAssets = import.meta.glob('./notes/assets/*', {
  eager: true,
  import: 'default',
