import DOMPurify from 'dompurify';
import { marked } from 'marked';

import { StorageKeys } from '@/core/types/common';
import { EXTENSION_VERSION } from '@/core/utils/version';
import { getCurrentLanguage, getTranslationSync } from '@/utils/i18n';
import type { AppLanguage } from '@/utils/language';

/**
 * Dynamically import all markdown changelog files.
 * Keyed by relative path, e.g. './notes/1.2.8.md'
 */
const changelogModules = import.meta.glob('./notes/*.md', {
  query: '?raw',
  import: 'default',
  eager: false,
}) as Record<string, () => Promise<string>>;

/**
 * Dynamically import all image assets so Vite resolves their URLs.
 */
const imageAssets = import.meta.glob('./notes/assets/*', {
  eager: true,
  import: 'default',
}) as Record<string, string>;

/**
 * Build a lookup map: filename (without path) → resolved URL
 * e.g. '1.2.8-hero.gif' → '/assets/1.2.8-hero-abc123.gif'
 */
function buildImageMap(): Record<string, string> {
  const map: Record<string, string> = {};
  for (const [path, url] of Object.entries(imageAssets)) {
    const filename = path.split('/').pop();
    if (filename) {
      map[filename] = url;
    }
  }
  return map;
}

/**
 * Parse optional YAML-like front matter from the markdown.
 * Returns the images map and the remaining body.
 *
 * Format:
 * ```
 * ---
 * images:
 *   hero: ./assets/1.2.8-hero.gif
 * ---
 * ```
 */
function parseFrontMatter(raw: string): {
  imageAliases: Record<string, string>;
  body: string;
} {
  const match = raw.match(/^---\r?\n([\s\S]*?)\r?\n---\r?\n([\s\S]*)$/);
  if (!match) {
    return { imageAliases: {}, body: raw };
  }

  const frontMatter = match[1];
  const body = match[2];
  const imageAliases: Record<string, string> = {};

  // Simple YAML parser for the images block
  let inImages = false;
  for (const line of frontMatter.split('\n')) {
    const trimmed = line.trim();
    if (trimmed === 'images:') {
      inImages = true;
      continue;
    }
    if (inImages && trimmed.includes(':')) {
      const colonIdx = trimmed.indexOf(':');
      const key = trimmed.slice(0, colonIdx).trim();
      let value = trimmed.slice(colonIdx + 1).trim();
      // Extract just the filename from paths like ./assets/1.2.8-hero.gif
      const filename = value.split('/').pop();
      if (key && filename) {
        value = filename;
        imageAliases[key] = value;
      }
    } else if (inImages && !trimmed.startsWith(' ') && trimmed.length > 0) {
      inImages = false;
    }
  }

  return { imageAliases, body };
}

/**
 * Extract the section matching the user's language from a multi-language
 * markdown file. Falls back to 'en' if the requested language is missing.
 */
export function extractLocalizedContent(raw: string, lang: AppLanguage): string {
  const { imageAliases, body } = parseFrontMatter(raw);

  // Split by <!-- lang:xx --> markers
  const sections = new Map<string, string>();
  const parts = body.split(/<!--\s*lang:(\w+)\s*-->/);

  // parts[0] is text before the first marker (usually empty)
  // parts[1] = lang code, parts[2] = content, parts[3] = lang code, etc.
  for (let i = 1; i < parts.length; i += 2) {
    const langCode = parts[i];
    const content = parts[i + 1]?.trim() ?? '';
    if (langCode && content) {
      sections.set(langCode, content);
    }
  }

  let content = sections.get(lang) ?? sections.get('en') ?? '';

  // Resolve image aliases: ![alt](key) → ![alt](resolved-url)
  if (Object.keys(imageAliases).length > 0) {
    const resolvedImages = buildImageMap();
    content = content.replace(/!\[([^\]]*)\]\(([^)]+)\)/g, (_match, alt: string, key: string) => {
      const filename = imageAliases[key];
      if (filename && resolvedImages[filename]) {
        return `![${alt}](${resolvedImages[filename]})`;
      }
      // Try direct filename match
      if (resolvedImages[key]) {
        return `![${alt}](${resolvedImages[key]})`;
      }
      return _match;
    });
  }

  return content;
}

/**
 * Get the docs URL for the current language.
 * zh is the root locale (no prefix), others use /{locale}/ prefix.
 */
function getDocsUrl(lang: AppLanguage): string {
  const base = 'https://voyager.nagi.fun';
  const path = '/guide/getting-started';
  if (lang === 'zh') return `${base}${path}`;
  return `${base}/${lang}${path}`;
}

/**
 * Render the changelog modal DOM.
 */
function createChangelogModal(
  htmlContent: string,
