 26→}) as Record<string, string>;
 27→
 28→/**
 29→ * Build a lookup map: filename (without path) → resolved URL
 30→ * e.g. '1.2.8-hero.gif' → '/assets/1.2.8-hero-abc123.gif'
 31→ */
 32→function buildImageMap(): Record<string, string> {
 33→  const map: Record<string, string> = {};
 34→  for (const [path, url] of Object.entries(imageAssets)) {
 35→    const filename = path.split('/').pop();
 36→    if (filename) {
 37→      map[filename] = url;
 38→    }
 39→  }
 40→  return map;
 41→}
