 43→/**
 44→ * Parse optional YAML-like front matter from the markdown.
 45→ * Returns the images map and the remaining body.
 46→ *
 47→ * Format:
 48→ * ```
 49→ * ---
 50→ * images:
 51→ *   hero: ./assets/1.2.8-hero.gif
 52→ * ---
 53→ * ```
 54→ */
 55→function parseFrontMatter(raw: string): {
 56→  imageAliases: Record<string, string>;
 57→  body: string;
 58→} {
 59→  const match = raw.match(/^---\r?\n([\s\S]*?)\r?\n---\r?\n([\s\S]*)$/);
 60→  if (!match) {
 61→    return { imageAliases: {}, body: raw };
 62→  }
 63→
 64→  const frontMatter = match[1];
 65→  const body = match[2];
 66→  const imageAliases: Record<string, string> = {};
 67→
 68→  // Simple YAML parser for the images block
 69→  let inImages = false;
 70→  for (const line of frontMatter.split('\n')) {
 71→    const trimmed = line.trim();
 72→    if (trimmed === 'images:') {
 73→      inImages = true;
 74→      continue;
 75→    }
 76→    if (inImages && trimmed.includes(':')) {
 77→      const colonIdx = trimmed.indexOf(':');
 78→      const key = trimmed.slice(0, colonIdx).trim();
 79→      let value = trimmed.slice(colonIdx + 1).trim();
 80→      // Extract just the filename from paths like ./assets/1.2.8-hero.gif
 81→      const filename = value.split('/').pop();
 82→      if (key && filename) {
 83→        value = filename;
 84→        imageAliases[key] = value;
 85→      }
 86→    } else if (inImages && !trimmed.startsWith(' ') && trimmed.length > 0) {
 87→      inImages = false;
 88→    }
 89→  }
 90→
 91→  return { imageAliases, body };
 92→}
