 94→/**
 95→ * Extract the section matching the user's language from a multi-language
 96→ * markdown file. Falls back to 'en' if the requested language is missing.
 97→ */
 98→export function extractLocalizedContent(raw: string, lang: AppLanguage): string {
 99→  const { imageAliases, body } = parseFrontMatter(raw);
100→
101→  // Split by <!-- lang:xx --> markers
102→  const sections = new Map<string, string>();
103→  const parts = body.split(/<!--\s*lang:(\w+)\s*-->/);
104→
105→  // parts[0] is text before the first marker (usually empty)
106→  // parts[1] = lang code, parts[2] = content, parts[3] = lang code, etc.
107→  for (let i = 1; i < parts.length; i += 2) {
108→    const langCode = parts[i];
109→    const content = parts[i + 1]?.trim() ?? '';
110→    if (langCode && content) {
111→      sections.set(langCode, content);
112→    }
113→  }
114→
115→  let content = sections.get(lang) ?? sections.get('en') ?? '';
116→
117→  // Resolve image aliases: ![alt](key) → ![alt](resolved-url)
118→  if (Object.keys(imageAliases).length > 0) {
119→    const resolvedImages = buildImageMap();
120→    content = content.replace(/!\[([^\]]*)\]\(([^)]+)\)/g, (_match, alt: string, key: string) => {
121→      const filename = imageAliases[key];
122→      if (filename && resolvedImages[filename]) {
123→        return `![${alt}](${resolvedImages[filename]})`;
124→      }
125→      // Try direct filename match
126→      if (resolvedImages[key]) {
127→        return `![${alt}](${resolvedImages[key]})`;
128→      }
129→      return _match;
130→    });
131→  }
132→
133→  return content;
134→}
